import React from "react"
import TodoItem from "./TodoItem"
import todosData from "./todosData"

class ListApp extends React.Component {
	constructor() {
		super()
		this.state = {
			todos: todosData
		}
		this.handleChange = this.handleChange.bind(this)
	}

	handleChange(id) {
		this.setState(prevState => {
			const i = id - 1
			let fixedTodos = prevState.todos
			fixedTodos[i].completed = !(prevState.todos[i].completed)
			return {
				todos: fixedTodos
			}
		})
	}

	render() {
		const todoItems = this.state.todos.map(item => <TodoItem key={item.id} item={item} changeHandler={this.handleChange}/>)

		return (
			<div className="todo-list">
				{todoItems}
        	</div>
		)
	}
	
}

export default ListApp
