import React from "react"
import TodoItem from "./TodoItem"
import todosData from "./todosData"

class ListApp extends React.Component {
	constructor() {
		super()
		this.state = {
			todos: todosData
		}
		this.handleChange = this.handleChange.bind(this)
	}

	handleChange(id) {
		this.setState(prevState => {
			const fixedTodos = prevState.todos.map( item => {
				let newItem = item
				if(item.id === id) {
					newItem.completed = !item.completed
				}
				return newItem
			})
			return {
				todos: fixedTodos
			}
		})
	}

	render() {
		const todoItems = this.state.todos.map(item => <TodoItem key={item.id} item={item} changeHandler={this.handleChange}/>)

		return (
			<div className="todo-list">
				{todoItems}
        	</div>
		)
	}
	
}

export default ListApp
